mapper.select:
{
	CALL getMember( 
		#{ ID, mode=IN }, 
		#{ ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=memberMap } 
	)
}

{
		CALL memberFindId(
#{NAME},#{ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=memberMap}
							)
}

{
		CALL memberFindPw(
#{ID},#{ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=memberMap}
							)
}

{ CALL memberQnaList (
 #{ ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=memberMap } 
 )}

mapper.update:
{ 
	CALL memberUpdate( 
		#{mvo.ID}, #{mvo.PWD}, #{mvo.NAME}, #{mvo.EMAIL}, #{mvo.PHONE}, 
		#{mvo.ZIP_NUM},#{mvo.ADDRESS1}, #{mvo.ADDRESS2}, #{mvo.ADDRESS3}, #{mvo.NICK},
		#{ ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=memberMap } 
	) 
}
